        KnowledgeAgentEventListener l = new KnowledgeAgentEventListener() {
            
            public void resourceCompilationFailed(ResourceCompilationFailedEvent event) {
            }
            
            public void knowledgeBaseUpdated(KnowledgeBaseUpdatedEvent event) {
