			else {
				targets = threats(opponentId, (TargetPermanent) target, game);
			}
			for (Permanent permanent: targets) {
				if (target.canTarget(permanent.getId(), game)) {
					target.addTarget(permanent.getId(), game);
					return true;
				}
			}
		}
