										if (this.activateAbility(ability, game))
											return;
									}
								}
							}
						}
					}
					break;
			}
		}
		else {
			//respond to opponent events
			switch (game.getTurn().getStep()) {
				case UPKEEP:
					findPlayables(game);
				case DECLARE_ATTACKERS:
					playRemoval(game.getCombat().getAttackers(), game);
					playDamage(game.getCombat().getAttackers(), game);
				case END_COMBAT:
					playDamage(game.getCombat().getAttackers(), game);
			}
		}
		this.passed = true;
	}

