        newIds.put(OsmPrimitiveType.WAY, new HashMap<Long, Long>());
        newIds.put(OsmPrimitiveType.RELATION, new HashMap<Long, Long>());

        for (PrimitiveData data : pasteBuffer.getAll()) {
