            newIds.get(data.getType()).put(data.getUniqueId(), copy.getUniqueId());

            bufferCopy.add(copy);
            if (pasteBuffer.getDirectlyAdded().contains(data)) {
                toSelect.add(copy);
            }
        }
        return newIds;
