    String path;
    boolean noQuery = StringUtil.isEmpty(query);
    if (!noQuery) {
      query = encodeUrl(query);
      path = "/legacy/issues/search/" + getRepoAuthor() + "/" + getRepoName() + "/open/" + encodeUrl(query);
