      dynamicCommands_.add(commands.goToFunctionDefinition());
      dynamicCommands_.add(commands.backToPreviousLocation());
      dynamicCommands_.add(commands.setWorkingDirToActiveDoc());
      for (AppCommand command : dynamicCommands_)
      {
