     */
    protected String getService(UMOEndpointURI uri)
    {
        String pathInfo = uri.getPath();

        if (pathInfo == null || "".equals(pathInfo)) {
            return uri.getHost();
        }

        String serviceName;

        int i = pathInfo.lastIndexOf("/");

        if (i > -1) {
            serviceName = pathInfo.substring(i + 1);
        }
        else {
            serviceName = pathInfo;
        }

        return serviceName;
    }

