            String dnc = OpenCms.getDefaultEncoding().trim();
            String enc = cms.readProperty(cms.readAbsolutePath(page), I_CmsConstants.C_PROPERTY_CONTENT_ENCODING, true, dnc).trim();

            cms.getRequestContext().setEncoding(enc);      
            if (DEBUG > 1) {
