    Clusters fsm = mock(Clusters.class);
    Cluster oneClusterMock = mock(Cluster.class);
    when(fsm.getCluster(anyString())).thenReturn(oneClusterMock);
    ActionDBAccessorImpl db = mock(ActionDBAccessorImpl.class);
