/**
   * Overrides the parent implementation to compose the final string
   * expression for the join clauses.
   */
  @Override
  public String joinClauseString() {
    NSMutableSet<String> seenIt = new NSMutableSet<String>();
    StringBuilder sb = new StringBuilder();
    JoinClause jc;
    EOSortOrdering.sortArrayUsingKeyOrderArray(_alreadyJoined,
        new NSArray<EOSortOrdering>(EOSortOrdering.sortOrderingWithKey("sortKey", EOSortOrdering.CompareCaseInsensitiveAscending)));
    if (_alreadyJoined.count() > 0) {
      jc = _alreadyJoined.objectAtIndex(0);

      sb.append(jc);
      seenIt.add(jc.table1);
      seenIt.add(jc.table2);
    }

    for (int i = 1; i < _alreadyJoined.count(); i++) {
      jc = _alreadyJoined.objectAtIndex(i);

      sb.append(jc.op);
      if (!seenIt.contains(jc.table1)) {
        sb.append(jc.table1);
        seenIt.add(jc.table1);
      } else if (!seenIt.contains(jc.table2)) {
        sb.append(jc.table2);
        seenIt.add(jc.table2);
      }
      sb.append(jc.joinCondition);
    }
    return sb.toString();
  }

