@Override
  public NSArray _foreignKeyConstraintStatementsForEntityGroup(NSArray<EOEntity> group) {
    if (group == null)
      return NSArray.EmptyArray;
    NSMutableArray<PostgresqlExpression> result = new NSMutableArray<PostgresqlExpression>();
    NSMutableSet<NSArray<String>> generatedStatements = new NSMutableSet<NSArray<String>>();
    for (EOEntity currentEntity : group) {
      if (currentEntity.externalName() != null) {
        NSArray<EORelationship> relationships = currentEntity.relationships();
        for (EORelationship currentRelationship : relationships) {
          if (_shouldGenerateForeignKeyConstraints(currentRelationship)) {
            NSArray<PostgresqlExpression> statements = foreignKeyConstraintStatementsForRelationship(currentRelationship);
            if (!generatedStatements.containsObject(statements.valueForKey("statement"))) {
              result.addObjectsFromArray(statements);
              generatedStatements.addObject(statements.valueForKey("statement"));
            }
          }
        }
      }
    }
    return result;
  }

