private String getLoggerName(Host host, String ctx) {
        String loggerName = "org.apache.catalina.core.ContainerBase.[default].[";
        if (host == null) {
            loggerName += getHost().getName();
        } else {
            loggerName += host.getName();
        }
        loggerName += "].[";
        loggerName += ctx;
        loggerName += "]";
        return loggerName;
    }

