        if (userBlocks.isEmpty()) {
          mUserIdToBlockIdsMap.remove(userId);
        }
        mAvailableBytes += block.getBlockSize();
        Preconditions.checkState(mCapacityBytes >= 0, "Capacity bytes should always be "
            + "non-negative");
        return true;
      }
    }
    return false;
  }

