			final ORecordInternal<?> iRecord) {
		checkConnection();

		do {
			boolean locked = lock.acquireExclusiveLock();

			try {
				final ORID rid = iRecord.getIdentity();

				try {
					beginRequest(OChannelBinaryProtocol.REQUEST_DICTIONARY_PUT);
					network.writeString(iKey);
