			switch (request) {
			case OChannelDistributedProtocol.PUSH_DISTRIBUTED_CONFIG:
				storage.updateClusterConfiguration(storage.getNetwork().readBytes());
				break;
			}
		} catch (Exception e) {
		} finally {
			storage.getNetwork().endResponse();
		}
	}

