private static String preprocessParam(String param)
    {
        param = param.toUpperCase();
        if (param.startsWith("'") && param.endsWith("'")) {
            param = param.substring(1, param.length()-1);
        }
        if (param.charAt(0)=='"' && param.charAt(param.length()-1)=='"') {
            param = param.substring(1, param.length()-1);
        }
        param = param.trim();
        return param;
    }

