      AbstractTreeNode node = (AbstractTreeNode)node1;
      if (nodeIsAcceptableForElement(node, element)) return node;
      AbstractTreeNode nodeResult = performDeepSearch(getChildren(node), element);
      if (nodeResult != null) {
        return nodeResult;
      }
    }
    return null;
  }

