	    					 // Update retrieved files
	    					updateRetrievedFiles();
    			            if(log.isDetailed()) log.logDetailed(toString(), Messages.getString("JobEntryFTP.GotFile", filelist[i])); //$NON-NLS-1$
	    					
	    					if(isaddresult)
	    					{
	    						FileObject targetFile = null;
