    GrExpression qualifier = getQualifierExpression();
    if (qualifier == null) {
      ResolveUtil.treeWalkUp(this, processor);
      qualifier = getRuntimeQualifier(this);
      if (qualifier != null) getVariantsFromQualifier(processor, qualifier);
