            return; // only run for top-level deployments
        }

        final CompositeIndex index = deploymentUnit.getAttachment(Attachments.COMPOSITE_ANNOTATION_INDEX);

        addAnnotations(deploymentUnit, CdiAnnotations.BUILT_IN_SCOPES);
