            Class<?> type = annotatedMethod.getParameterTypes()[0];
            //we don't want support for "--flag true" syntax
            if (type == Boolean.class || type == Boolean.TYPE) {
                argumentType = Void.TYPE;
            }else{
                argumentType = type;
            }
            availableValues = new ArrayList<String>();
        } else {
