            if (type == Boolean.class || type == Boolean.TYPE) {
                argumentType = Void.TYPE;
            }else{
                argumentType = type;
                if(argumentType.isEnum()){
                    final Enum[] enumConstants = (Enum[]) argumentType.getEnumConstants();
                    for (Enum enumConstant : enumConstants) {
