      dbServer3 = new ODatabaseDocumentTx(getPlocalDatabaseURL(serverInstance.get(2))).open("admin","admin");
      firstInsert = new ODocument("Hero").fields("id", "R001", "firstName", "Luke", "lastName", "Skywalker").save();
