    recordCount = dbServer1.countClass("Hero");
    assertEquals(1, recordCount);
    ODatabaseRecordThreadLocal.INSTANCE.set(dbServer2);
    recordCount = dbServer2.countClass("Hero");
    assertEquals(1, recordCount);

