		int j = 0;
		for (Iterator it = frags.iterator(); it.hasNext(); ++j) {
			final Object o = it.next();
			if (o instanceof ExpressionFragment) {
				fs[j] = compile(((ExpressionFragment)o).getExpression(), pctx);
