        if (parentType == FUNCTION_LITERAL && child1Type == LBRACE) {
            return Spacing.createSpacing(1, 1, 0, mySettings.KEEP_LINE_BREAKS, mySettings.KEEP_BLANK_LINES_IN_CODE);
        }

        return null;
    }

