                if (isNonStandard(node, DEFAULT_CALLBACK_HANDLER_CLASS_NAME)) {
                    writeAttribute(writer, Attribute.DEFAULT_CALLBACK_HANDLER_CLASS_NAME,
                            node.get(DEFAULT_CALLBACK_HANDLER_CLASS_NAME));
                }
