                "        0,\n" +
                "        [\n" +
                "            \"one\",\n" +
                "            [\n" +
                "                2,\n" +
                "                %d,\n" +
                "                4.5\n" +
                "            ]\n" +
                "        ],\n" +
                "        {\n" +
                "            \"veggies\": \"good for you\",\n" +
                "            \"dairy\": \"%d\",\n" +
                "            \"numeric\": 5.6\n" +
                "        }\n" +
                "    ],\n" +
                "    \"dot.char\": \"foo.bar\",\n" +
                "    \"bracket][[] [ ] chars\": \"[foo]\",\n" +
                "    \"tag\": \"%s\"\n" +
                "}";

