private boolean setupRequest()
    {
        long version = slave.getVersion();
        while ( version < masterVersion )
        {
            if ( slave.hasLog( version ) )
            {
                version++;
            }
            else
            {
                break;
            }
        }
        if ( version == masterVersion )
        {
            setStatus( Status.GET_MESSAGE );
            return true;
        }
        if ( retries > 20 )
        {
            close();
        }
        if ( !acquireWriteBuffer() )
