        while ( xaDs.hasLogicalLog( nextVersion ) )
        {
            try
            {
                xaDs.applyLog( xaDs.getLogicalLog( nextVersion ) );
            }
            catch ( IOException e )
            {
                throw new UnderlyingStorageException( 
                    "Unable to recover slave to consistent state", e );
            }
            nextVersion++;
        }
    }

