private void sendPacket(byte[] data, Peer replyTo, PeerNode pn, int alreadyReportedBytes) throws LocalAddressException {
		if(pn.isIgnoreSource()) {
			Peer p = pn.getPeer();
			if(p != null) replyTo = p;
		}
