   */
  TreeRect searchRect(final int rn, final int lv, final int pre) {
    final TreeBorder bo = getTreeBorder(rn, lv);

    int i = searchPreArrayPos(bo.level, pre);

    return i == -1 ? null : rects[rn][lv][i - bo.start];
  }

