      final int p = parentList[i];
      final ChildIterator iterator = new ChildIterator(data, p);

      while(iterator.more()) {
        final int pre = iterator.next();

        if(data.kind(pre) == Data.ELEM || !ONLY_ELEMENT_NODES) {
          temp.add(pre);
        }
      }
    }
    return temp.finish();
  }

