private String stringForIndex( long index )
    {
        int len = ((int) index) % MAX_CONTENT_SIZE + 1;
        StringBuilder str = new StringBuilder( len );

        while ( len-- > 0 )
        {
            str.append( CHARS.charAt( len % CHARS.length() ) );
        }

        return str.toString();
    }

