        if (virtualNodes.isSelected()) {
            if (vn < 1) {
                vn = 8;
            }
        }
        else {
            vn = 0;
        }
        Main.pref.putInteger("mappaint.node.virtual-size", vn);
        Main.pref.putInteger("draw.rawgps.layer.wpt", waypointLabel.getSelectedIndex());
        return false;
    }

