			return null;
		LldpLink lldplink = new LldpLink();
		lldplink.setLldpLocalPortNum(lldpRemLocalPortNum);
		lldplink.setLldpPortId(LldpRemTableTracker.decodeLldpPortId(val[0].toInt(),val[1]));
