        String user = msg.getStringProperty(MuleProperties.MULE_USER_PROPERTY, PropertyScope.OUTBOUND, null);
        if (user != null)
        {
            response.setHeader(new Header(CUSTOM_HEADER_PREFIX + MuleProperties.MULE_USER_PROPERTY, user));
        }
