				// check if url is allowed to view
				if (sb.getConfig("proxyURL.rewriteURLs", "all").equals("domainlist")) {
					try {
						if (sb.crawlStacker.urlInAcceptedDomain(new DigestURL (modifyURL)) == null) {
							modifyURL = "./proxy.html?url="+modifyURL;
