                prop.put("content_item_urlhash", ASCII.String(image.imageUrl.hash()));
                prop.put("content_item_source", image.sourceUrl.toNormalform(true));
                prop.putXML("content_item_source-xml", image.sourceUrl.toNormalform(true));
