                } else {
                	outsb.append("Given string seems to be an URL, loading...\r\n");
                    URL url = new URL(key);
                    URLConnection uc = url.openConnection();
                	in = new BufferedReader(
                			// FIXME get charset from uc.getContentType()
                			new InputStreamReader(uc.getInputStream()));
