            throw new ClientComputeException( "InvalidSnapshot.NotFound", "The snapshot '"+request.getSnapshotId( )+"' does not exist." );
        } catch ( ExecutionException ex1 ) {
            throw new EucalyptusCloudException( ex1.getCause( ) );
