		ResultSet rs = stmt.executeQuery();

		if (!rs.next()) {
			rs.close();
			stmt.close();
			if (log.isDebugEnabled())
				log
						.debug("getIfIndexByName: no entries found in snmpinterface");
			return -1;
		}

		// extract the values.
