		int ndx = 1;

		if (rs.wasNull()) {
			
			if (log.isDebugEnabled())
				log
						.debug("getIfIndexByName: no entries found in snmpinterface");
			return -1;
			
		}

		int ifindex = rs.getInt(ndx++);

		if (log.isDebugEnabled())
			log.debug("getIfIndexByName: found ifindex="
					+ ifindex);

		stmt.close();

		return ifindex;
	}

