public void render(Component comp, Writer out) throws IOException {
		final WriterHelper wh = new WriterHelper(out);
		final Window self = (Window)comp;
		final String uuid = self.getUuid();
		final Execution exec = Executions.getCurrent();

		wh.write("<div id=\"");
		wh.write(uuid);
		wh.write("\" z.type=\"zul.wnd.Wnd\" z.autoz=\"true\"");
		wh.write(self.getOuterAttrs());
		wh.write(self.getInnerAttrs());
		wh.write(">\n");

		final Caption caption = self.getCaption();
		final String title = self.getTitle(), titlesc = self.getTitleSclass();
		String wcExtStyle = "";
		if (caption == null && title.length() == 0) {
			if (exec.isExplorer() && !exec.isExplorer7()) {
				wh.write(
					"<table width=\"100%\" border=\"0\" cellpadding=\"0\" cellspacing=\"0\">"
					+"<tr height=\"1px\"><td></td></tr></table>\n");
			}
		} else {
			wh.write("<table width=\"100%\" border=\"0\" cellpadding=\"0\" cellspacing=\"0\">\n");
			if (caption == null) {
				wh.write("<tr id=\"");
				wh.write(uuid);
				wh.write("!caption\" class=\"title\">\n");
				wh.write("<td class=\"l");
				wh.write(titlesc);
				wh.write("\"></td>\n");
				wh.write("<td class=\"m");
				wh.write(titlesc);
				wh.write("\">");
				wh.write(XMLs.escapeXML(title));
				wh.write("</td>");
				if (self.isClosable()) {
					wh.write("<td width=\"16\" class=\"m");
					wh.write(titlesc);
					wh.write("\"><img id=\"");
					wh.write(uuid);
					wh.write("!close\" src=\"");
					wh.write(exec.encodeURL("~./zul/img/close-off.gif"));
					wh.write("\"/></td>");
				}
				wh.write("<td class=\"r");
				wh.write(titlesc);
				wh.write("\"></td></tr>\n");
			} else {
				wh.write("<tr id=\"");
				wh.write(uuid);
				wh.write("!caption\"><td class=\"l");
				wh.write(titlesc);
				wh.write("\"></td><td class=\"m");
				wh.write(titlesc);
				wh.write("\">");
				caption.redraw(out);
				wh.write("</td><td class=\"r");
				wh.write(titlesc);
				wh.write("\"></td></tr>\n");
			}
			wh.write("</table>\n");
			wcExtStyle = "border-top:0;";
		}

		final String cs = self.getContentStyle();
		if(cs!=null){
			wcExtStyle += cs;
		}
		wh.write("<div id=\"").write(uuid).write("!cave\" class=\"")
			.write(self.getContentSclass()).write("\"")
			.writeAttr("style", wcExtStyle).write(">");

		for (Iterator it = self.getChildren().iterator(); it.hasNext();) {
			final Component child = (Component)it.next();
			if (child != caption)
				child.redraw(out);
		}
		wh.write("</div></div>\n");
	}

