    void _register(String group, Address mbr) {
        Socket sock;
        ObjectOutputStream out;
        IpAddress entry;
        GossipData gossip_req;

        for(int i=0; i < gossip_servers.size(); i++) {
            entry=(IpAddress) gossip_servers.elementAt(i);
