        return true;
      }
      if (defaultAVD.create(sdk)) {
        System.out.println("the avd was created");
        return true;
      }
      Base.showWarning("Android Error", AVD_CREATE_ERROR, null);
    } catch (final Exception e) {
      Base.showWarning("Android Error", AVD_CREATE_ERROR, e);
    }
    return false;
  }

