    Key key = Vec.newKey();
    AppendableVec av = new AppendableVec(key);
    NewChunk nv = new NewChunk(av,0);
    for(double d:vals){
      if(Double.isNaN(d))nv.addNA();
      else if((long)d == d) nv.addNum((long)d,0);
      else nv.addNum(d);
    }
    nv.close(0,null);
