public static String insert(String s, String text, int offset) {
		if ((s == null) || (text == null)) {
			return null;
		}

		try {
			StringBuilder sb = new StringBuilder(s);
			sb.insert(offset, text);
			return sb.toString();
		}
		catch (StringIndexOutOfBoundsException siobe) {
			return null;
		}
	}

