			if (urlParams.length >= 4) {
				addParam(params, "jspPage", "/admin/view_article.jsp");
				addParam(params, "resourcePrimKey", urlParams[3]);
			}

			if (urlParams.length >= 5) {
				addParam(params, "p_p_state", urlParams[4]);
			}
		}
	}

